import React, { useState } from 'react'
import { Row, Col, Container, Form } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import "./Login.css"
function Login() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')

  const onChangeEmail = (e) => {
    const value = e.target.value
    setEmail(value)
  }
  const onChangePassword = (e) => {
    const value = e.target.value
    setPassword(value)
  }
  return (
    < Container >
      <Row>
        <Col md={5} className="Login_bg"></Col>
        <Col md={7} className="d-flex align-items-center justify-content-center flex-direction-column">
          <Form style={{ width: "80%", maxWidth: 500 }}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label>Email address</Form.Label>
              <Form.Control type="email" placeholder="Enter email" onChange={onChangeEmail} value={email} />
              <Form.Text className="text-muted">
                We'll never share your email with anyone else.
              </Form.Text>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control type="password" placeholder="Password" onChange={onChangePassword} value={password} />
            </Form.Group>
            <Button variant="primary" type="submit">
              Login
            </Button>
            <div className='py-4'>
              <p className='text-center'>
                Anda Tidak Memiliki Akun ? <Link to="/signup">Daftar</Link>
              </p>
            </div>
          </Form>
        </Col>
      </Row>
    </Container >
  );
}

export default Login;
